import React from "react"
// import styledSpecies from "./Species.module.css";

export default function Species({ species, handleSpecies, handleAllSpecies }) {
  return (
    <div>
    <h2>Species</h2>
      {species.map((especie) => {
        return <button key={especie} onClick={handleSpecies} value={especie}>{especie}</button>
      })}
      <button onClick={handleAllSpecies}>All nimals</button>
    </div>
  )
}
