  // })

  return (
    <div>
      <input onChange={handleInputChange} type="text" value={zoo.zooName} />
      <label>Zoo Name:</label>
      <h1 className={styledZoo.divContentTitle}>{zoo.zooName}</h1>
      <Species species={zoo}/>
      <Animals/>
    </div>
  )
}
