import React from "react"

const ContactUs = () => {
  const [state, setState] = React.useState({
    nombre: "",
    email: "",
    asunto: "",
    mensaje: "",
  })
  const handleInput = (e) => {
    alert("change")
    const value = e.target.value
    const name = e.target.name
    setState({ ...state, [name]: value })
  }

  return (
    <div>
      <form className="contactBg">
        <label onChange={handleInput} htmlFor="nombre">
          Nombre:{" "}
        </label>
        <input name="nombre" />
        <label onChange={handleInput} htmlFor="email">
          Email:{" "}
        </label>
        <input name="email" />
        <label onChange={handleInput} htmlFor="asunto">
          Asunto:{" "}
        </label>
        <input name="asunto" />
        <label onChange={handleInput} htmlFor="mensaje">
          Mensaje:{" "}
        </label>
        <input name="mensaje" />
        <button>Enviar</button>
      </form>
    </div>
  )
}

export default ContactUs
